mapper.select:
SELECT c.* FROM course c
JOIN user u ON c.teacher_id = u.user_id
WHERE 1=1

    AND (c.course_name LIKE CONCAT('%', #{keyword}, '%') OR u.name LIKE CONCAT('%', #{keyword}, '%'))


    AND c.course_type = #{courseType}


    AND u.dept = #{dept}


    AND c.teacher_id = #{teacherId}

ORDER BY c.${sortBy} ${order}
